{-# LANGUAGE OverloadedStrings #-}

module Main where

import Data.ByteString (ByteString)
import Data.ByteString.Base16

import Network.QUIC

dec16 :: ByteString -> ByteString
dec16 = fst . decode

enc16 :: ByteString -> ByteString
enc16 = encode

main :: IO ()
main = do
    test1
    test2

test1 :: IO ()
test1 = do
    ctx <- serverContext "\x41\x6c\x50\xc4\x3e\x23\x48\x7c"
    decodePacket ctx clientInitial >>= print

test2 :: IO ()
test2 = do
    ctx <- clientContext "\x41\x6c\x50\xc4\x3e\x23\x48\x7c"
    decodePacket ctx serverInitial >>= print

clientInitial :: ByteString
clientInitial = dec16 ""

serverInitial :: ByteString
serverInitial = dec16 ""
